let createPost=function(){let t=$("#new-post-form");t.submit((function(e){e.preventDefault(),$.ajax({type:"post",url:"/posts/create",data:t.serialize(),success:function(t){console.log(t);let e=newPostDom(t.data.post,t.data.userDet);$("#posts-list-container>ol").prepend(e),deletePost($(" .delete-post-button",e)),new PostComments(t.data.post._id),new ToggleLike($(" .toggle-like-button",e)),new Noty({theme:"relax",text:"Post Published!",type:"success",layout:"topRight",timeout:500}).show()},error:function(t){console.log(t.responseText)}})}))},newPostDom=function(t,e){return $(`\n    <li id="post-${t._id}">\n    <p>\n      ${t.content}\n        <a class="delete-post-button" href="/posts/destroy/${t._id}">\n          <b>\n            <i class="fas fa-trash"></i>\n          </b>\n        </a>\n      <br>\n      <small>\n        Posted By : ${e.user.name}\n        <i>\n          (${e.user.email})\n        </i>\n      </small>\n      <br>\n      <small>\n          <a class="toggle-like-button" data-likes="0" href="/likes/toggle/?id=${t._id}&type=Post">\n            <i class="fa-regular fa-heart"></i> 0\n          </a>\n      </small>\n    </p>\n  \n    <div class="post-comments">\n      <form id="post-${t._id}-comments-form" action="/comments/create" method="post">\n        <input type="text" name="content" placeholder="Type Here To Add Comment" style="width: 200px;">\n        <input type="hidden" name="post" value="${t._id}">\n        <input type="submit" value="Add Comment">\n      </form>\n    \n      <div class="post-comments-list">\n        <ul id="post-comment-${t._id}" style="padding-left: 5px;">\n\n        </ul>\n      </div>\n    \n    </div>\n  \n    </li>`)},deletePost=function(t){$(t).click((function(e){e.preventDefault(),$.ajax({type:"get",url:$(t).prop("href"),success:function(t){$(`#post-${t.data.post_id}`).remove(),new Noty({theme:"relax",text:"Post Deleted!",type:"success",layout:"topRight",timeout:500}).show()},error:function(t){console.log(t.responseText)}})}))},convertPostsToAjax=function(){$("#posts-list-container>ol>li").each((function(){let t=$(this),e=$(" .delete-post-button",t);deletePost(e);let n=t.prop("id").split("-")[1];new PostComments(n)}))};createPost(),convertPostsToAjax();